import{_ as a}from"./chunks/add_search_filter.C3TcHSHJ.js";import{_ as t,o as r,c as s,ag as i}from"./chunks/framework.DPLcbSfg.js";const b=JSON.parse('{"title":"Scene Search Filter","description":"","frontmatter":{},"headers":[],"relativePath":"editor-guide/components/scene-search-filter.md","filePath":"editor-guide/components/scene-search-filter.md"}'),o={name:"editor-guide/components/scene-search-filter.md"};function n(l,e,c,d,p,h){return r(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="scene-search-filter" tabindex="-1">Scene Search Filter <a class="header-anchor" href="#scene-search-filter" aria-label="Permalink to &quot;Scene Search Filter&quot;">​</a></h1><p>SceneSearchFilters define which areas of your scene ProStream will process. They improve performance by limiting the scope to specific hierarchies.</p><div class="tip custom-block"><p class="custom-block-title">Purpose</p><p>SceneSearchFilters define which areas of your scene ProStream will process. They improve performance by limiting the scope to specific hierarchies.</p></div><div class="warning custom-block"><p class="custom-block-title">Important</p><p>All GameObjects must be Prefabs to be compatible with ProStream systems. The Prefab system is used to differentiate one GameObject hierarchy from another. Non-Prefab GameObjects are skipped by the matching systems.</p></div><h2 id="how-to-add-scene-search-filters" tabindex="-1">How to Add Scene Search Filters <a class="header-anchor" href="#how-to-add-scene-search-filters" aria-label="Permalink to &quot;How to Add Scene Search Filters&quot;">​</a></h2><h3 id="method-1-prostream-editor-recommended" tabindex="-1">Method 1: ProStream Editor (Recommended) <a class="header-anchor" href="#method-1-prostream-editor-recommended" aria-label="Permalink to &quot;Method 1: ProStream Editor (Recommended)&quot;">​</a></h3><ol><li>Open ProStream Editor: <strong>Tools | instance.id | ProStream | ProStream Editor</strong></li><li>Click the <strong>Add Search Filters</strong> button</li><li>A hierarchy selector window will open showing root-level GameObjects</li><li>Select one or more root GameObjects that contain your prefabs</li><li>Click <strong>Confirm</strong></li></ol><p><strong>What happens automatically:</strong></p><ul><li>ProStream adds <code>SceneSearchFilter</code> component to selected GameObjects</li><li>Locates all prefab instances under those GameObjects</li><li>Adds <code>MatchTracker</code> components to each prefab child</li><li>Sets tracker status to &quot;Searchable&quot;</li></ul><h3 id="method-2-context-menu-alternative" tabindex="-1">Method 2: Context Menu (Alternative) <a class="header-anchor" href="#method-2-context-menu-alternative" aria-label="Permalink to &quot;Method 2: Context Menu (Alternative)&quot;">​</a></h3><ol><li>Right-click on one or many top-level GameObjects in the scene hierarchy</li><li>Select <strong>ProStream | Add Scene Search Filter</strong></li></ol><p><img src="'+a+`" alt="Add Search Filter Context Menu"></p><p>This will add a SceneSearchFilter component to the GameObject and begin indexing the child prefab GameObjects.</p><h2 id="how-search-filters-work" tabindex="-1">How Search Filters Work <a class="header-anchor" href="#how-search-filters-work" aria-label="Permalink to &quot;How Search Filters Work&quot;">​</a></h2><p><strong>Hierarchy Structure:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Scene</span></span>
<span class="line"><span>├── Buildings (SearchFilter)</span></span>
<span class="line"><span>│   ├── Building_01 (Prefab → MatchTracker added)</span></span>
<span class="line"><span>│   ├── Building_02 (Prefab → MatchTracker added)</span></span>
<span class="line"><span>│   └── Building_03 (Prefab → MatchTracker added)</span></span>
<span class="line"><span>├── Props (SearchFilter)</span></span>
<span class="line"><span>│   ├── Prop_01 (Prefab → MatchTracker added)</span></span>
<span class="line"><span>│   └── Prop_02 (Prefab → MatchTracker added)</span></span>
<span class="line"><span>└── Camera (No filter → ignored)</span></span></code></pre></div><p><strong>Key Points:</strong></p><ul><li>Only prefab instances <strong>under</strong> search filter GameObjects are tracked</li><li>This allows organizing large scenes into manageable sections</li><li>Improves performance by limiting the scope of object tracking</li><li>You can have multiple search filters in a scene</li></ul><h2 id="matchtracker-component" tabindex="-1">MatchTracker Component <a class="header-anchor" href="#matchtracker-component" aria-label="Permalink to &quot;MatchTracker Component&quot;">​</a></h2><p>When you add a search filter, ProStream automatically adds <code>MatchTracker</code> components to all prefab children:</p><p><strong>MatchTracker Properties:</strong></p><ul><li><code>FilterId</code> - References parent SceneSearchFilter</li><li><code>MatchStatus</code> - Searchable, NotSearchable, or Converted</li><li><code>IsEnabled</code> - Whether tracker is active</li><li><code>IsMatched</code> - Whether a rule matched this object</li><li><code>SectionId</code> - Which streaming layer this object belongs to</li></ul><p><strong>You don&#39;t need to add MatchTracker manually</strong> - it&#39;s automatic.</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="organize-by-type" tabindex="-1">Organize by Type <a class="header-anchor" href="#organize-by-type" aria-label="Permalink to &quot;Organize by Type&quot;">​</a></h3><p>Create separate search filters for different object types:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Scene</span></span>
<span class="line"><span>├── Buildings (SearchFilter)</span></span>
<span class="line"><span>├── Vegetation (SearchFilter)</span></span>
<span class="line"><span>├── Props (SearchFilter)</span></span>
<span class="line"><span>├── Terrain (SearchFilter)</span></span>
<span class="line"><span>└── Lighting (No filter)</span></span></code></pre></div><h3 id="use-descriptive-names" tabindex="-1">Use Descriptive Names <a class="header-anchor" href="#use-descriptive-names" aria-label="Permalink to &quot;Use Descriptive Names&quot;">​</a></h3><p>Name your filter GameObjects clearly:</p><ul><li>&quot;Buildings_Residential&quot;</li><li>&quot;Vegetation_Trees&quot;</li><li>&quot;Props_Interactive&quot;</li><li>&quot;Terrain_Ground&quot;</li></ul><h3 id="limit-scope" tabindex="-1">Limit Scope <a class="header-anchor" href="#limit-scope" aria-label="Permalink to &quot;Limit Scope&quot;">​</a></h3><p>Don&#39;t add search filters to:</p><ul><li>Camera GameObjects</li><li>Lighting systems</li><li>UI elements</li><li>Audio sources</li><li>Manager objects</li></ul><h2 id="performance-considerations" tabindex="-1">Performance Considerations <a class="header-anchor" href="#performance-considerations" aria-label="Permalink to &quot;Performance Considerations&quot;">​</a></h2><p><strong>Smaller Scope = Better Performance</strong></p><ul><li>Fewer objects to scan during rule matching</li><li>Faster position calculation</li><li>Reduced memory usage during processing</li></ul><p><strong>Multiple Filters</strong></p><p>You can have as many search filters as needed:</p><ul><li>Each filter processes independently</li><li>Allows parallel processing in future updates</li><li>Easier to debug specific sections</li></ul><h2 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h2><p><strong>No objects found during Calculate Positions</strong></p><ul><li>Verify search filters are added</li><li>Check that objects under filters are prefabs</li><li>Ensure MatchTracker components were added</li></ul><p><strong>Too many objects matched</strong></p><ul><li>Narrow search filter scope</li><li>Split large filters into smaller ones</li><li>Check for unintended prefab instances</li></ul><p><strong>Objects not being processed</strong></p><ul><li>Verify object is under a search filter</li><li>Check that object is a prefab instance</li><li>Ensure MatchTracker status is &quot;Searchable&quot;</li></ul><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="/core-concepts/importance-of-prefabs.html">Importance of Prefabs</a> - Why prefabs are required</li><li><a href="/editor-guide/engines/rule-engine.html">Rule Engine</a> - How objects are matched</li><li><a href="/getting-started/standard-workflow.html">Standard Workflow</a> - Complete setup guide</li></ul>`,48)])])}const f=t(o,[["render",n]]);export{b as __pageData,f as default};
