import{_ as t,o as i,c as a,ag as l}from"./chunks/framework.DPLcbSfg.js";const g=JSON.parse('{"title":"Installation & Update Issues","description":"","frontmatter":{},"headers":[],"relativePath":"reference/install-update.md","filePath":"reference/install-update.md"}'),s={name:"reference/install-update.md"};function n(r,e,o,p,c,u){return i(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="installation-update-issues" tabindex="-1">Installation &amp; Update Issues <a class="header-anchor" href="#installation-update-issues" aria-label="Permalink to &quot;Installation &amp; Update Issues&quot;">​</a></h1><p>Common issues related to installing or updating ProStream.</p><h2 id="installation-issues" tabindex="-1">Installation Issues <a class="header-anchor" href="#installation-issues" aria-label="Permalink to &quot;Installation Issues&quot;">​</a></h2><h3 id="lost-references-missing-scripts" tabindex="-1">Lost References / Missing Scripts <a class="header-anchor" href="#lost-references-missing-scripts" aria-label="Permalink to &quot;Lost References / Missing Scripts&quot;">​</a></h3><p><strong>Issue:</strong> Components show as &quot;Missing Script&quot; after installation</p><p><strong>Cause:</strong> Installing ProStream while a scene with ProStream components is open</p><p><strong>Solution:</strong></p><ol><li>Close all scenes before installing</li><li>Install ProStream</li><li>Reopen scenes after installation completes</li><li>If references are still lost, you may need to re-run scene setup</li></ol><p><strong>Prevention:</strong></p><ul><li>Always close scenes before installing packages</li><li>Save all work before installation</li><li>Restart Unity after installation if issues persist</li></ul><h3 id="package-installation-fails" tabindex="-1">Package Installation Fails <a class="header-anchor" href="#package-installation-fails" aria-label="Permalink to &quot;Package Installation Fails&quot;">​</a></h3><p><strong>Issue:</strong> Unity Package Manager fails to install ProStream</p><p><strong>Common Causes:</strong></p><ul><li>Network connectivity issues</li><li>Package cache corruption</li><li>Unity version incompatibility</li><li>Insufficient permissions</li></ul><p><strong>Solutions:</strong></p><ol><li>Check Unity version meets requirements (2022.3.0f1+)</li><li>Clear Package Manager cache</li><li>Restart Unity</li><li>Check internet connection</li><li>Try manual package installation</li></ol><h3 id="dependencies-not-installed" tabindex="-1">Dependencies Not Installed <a class="header-anchor" href="#dependencies-not-installed" aria-label="Permalink to &quot;Dependencies Not Installed&quot;">​</a></h3><p><strong>Issue:</strong> Required packages (Entities, etc.) not installed automatically</p><p><strong>Solution:</strong></p><ol><li>Open Package Manager</li><li>Manually install required packages: <ul><li>Unity Entities</li><li>Unity Burst</li><li>Unity Collections</li><li>Unity Mathematics</li></ul></li><li>Restart Unity</li><li>Re-run ProStream installer</li></ol><h2 id="update-issues" tabindex="-1">Update Issues <a class="header-anchor" href="#update-issues" aria-label="Permalink to &quot;Update Issues&quot;">​</a></h2><h3 id="objectdisposedexception-errors" tabindex="-1">ObjectDisposedException Errors <a class="header-anchor" href="#objectdisposedexception-errors" aria-label="Permalink to &quot;ObjectDisposedException Errors&quot;">​</a></h3><p><strong>Issue:</strong> Constant &quot;UNKNOWN_OBJECT_TYPE has been deallocated&quot; errors after update</p><p><strong>Cause:</strong> Updating ProStream (specifically the Entities package) while a scene with SubScene components is open</p><p><strong>Solution:</strong></p><ol><li>Close all scenes</li><li>Update packages</li><li>Restart Unity</li><li>Reopen scenes</li><li>If errors persist, reimport ProStream</li></ol><p><strong>Prevention:</strong></p><ul><li>Always close scenes before updating</li><li>Close Unity before major updates</li><li>Backup project before updates</li></ul><h3 id="settings-lost-after-update" tabindex="-1">Settings Lost After Update <a class="header-anchor" href="#settings-lost-after-update" aria-label="Permalink to &quot;Settings Lost After Update&quot;">​</a></h3><p><strong>Issue:</strong> ProStream settings reset after update</p><p><strong>Cause:</strong> Settings format changed between versions</p><p><strong>Solution:</strong></p><ol><li>Reconfigure settings in Settings Panel</li><li>Re-run scene setup if needed</li><li>Check scene-specific settings in SceneSettings asset</li></ol><h3 id="compilation-errors-after-update" tabindex="-1">Compilation Errors After Update <a class="header-anchor" href="#compilation-errors-after-update" aria-label="Permalink to &quot;Compilation Errors After Update&quot;">​</a></h3><p><strong>Issue:</strong> C# compilation errors after updating</p><p><strong>Common Causes:</strong></p><ul><li>API changes between versions</li><li>Incompatible package versions</li><li>Cached assemblies</li></ul><p><strong>Solutions:</strong></p><ol><li>Restart Unity</li><li>Reimport all assets</li><li>Clear Library folder (Unity will rebuild)</li><li>Check for package version conflicts</li><li>Update all related packages</li></ol><h2 id="version-compatibility" tabindex="-1">Version Compatibility <a class="header-anchor" href="#version-compatibility" aria-label="Permalink to &quot;Version Compatibility&quot;">​</a></h2><h3 id="unity-version-requirements" tabindex="-1">Unity Version Requirements <a class="header-anchor" href="#unity-version-requirements" aria-label="Permalink to &quot;Unity Version Requirements&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ProStream Version</th><th>Minimum Unity Version</th><th>Recommended Unity Version</th></tr></thead><tbody><tr><td>Current</td><td>2022.3.0f1</td><td>Unity 6.1</td></tr></tbody></table><h3 id="package-dependencies" tabindex="-1">Package Dependencies <a class="header-anchor" href="#package-dependencies" aria-label="Permalink to &quot;Package Dependencies&quot;">​</a></h3><p>Required packages:</p><ul><li>Unity Entities (compatible version)</li><li>Unity Burst</li><li>Unity Collections</li><li>Unity Mathematics</li><li>Scriptable Render Pipeline (URP or HDRP)</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="before-installing-updating" tabindex="-1">Before Installing/Updating <a class="header-anchor" href="#before-installing-updating" aria-label="Permalink to &quot;Before Installing/Updating&quot;">​</a></h3><ol><li>Backup your project</li><li>Close all scenes</li><li>Save all work</li><li>Check Unity version compatibility</li><li>Read release notes</li><li>Close Unity (for major updates)</li></ol><h3 id="after-installing-updating" tabindex="-1">After Installing/Updating <a class="header-anchor" href="#after-installing-updating" aria-label="Permalink to &quot;After Installing/Updating&quot;">​</a></h3><ol><li>Restart Unity</li><li>Check Console for errors</li><li>Verify package installation</li><li>Test in a simple scene first</li><li>Reconfigure settings if needed</li><li>Re-run scene setup if required</li></ol><h3 id="safe-update-process" tabindex="-1">Safe Update Process <a class="header-anchor" href="#safe-update-process" aria-label="Permalink to &quot;Safe Update Process&quot;">​</a></h3><ol><li><p><strong>Backup Project</strong></p><ul><li>Use version control (Git)</li><li>Or create a complete copy</li></ul></li><li><p><strong>Close Everything</strong></p><ul><li>Close all scenes</li><li>Close Unity</li></ul></li><li><p><strong>Update</strong></p><ul><li>Update ProStream package</li><li>Update dependencies if needed</li></ul></li><li><p><strong>Restart</strong></p><ul><li>Restart Unity</li><li>Let it recompile</li></ul></li><li><p><strong>Verify</strong></p><ul><li>Check for errors</li><li>Test basic functionality</li><li>Verify existing scenes work</li></ul></li><li><p><strong>Reconfigure</strong></p><ul><li>Check settings</li><li>Re-run setup if needed</li></ul></li></ol><h2 id="troubleshooting-installation" tabindex="-1">Troubleshooting Installation <a class="header-anchor" href="#troubleshooting-installation" aria-label="Permalink to &quot;Troubleshooting Installation&quot;">​</a></h2><h3 id="installer-not-appearing" tabindex="-1">Installer Not Appearing <a class="header-anchor" href="#installer-not-appearing" aria-label="Permalink to &quot;Installer Not Appearing&quot;">​</a></h3><p><strong>Issue:</strong> ProStream installer window doesn&#39;t open after import</p><p><strong>Solution:</strong></p><ol><li>Open manually: <strong>Tools &gt; instance.id &gt; ProStream &gt; (Install/Update) ProStream</strong></li><li>Check Console for errors</li><li>Verify package imported correctly</li><li>Restart Unity</li></ol><h3 id="installation-hangs" tabindex="-1">Installation Hangs <a class="header-anchor" href="#installation-hangs" aria-label="Permalink to &quot;Installation Hangs&quot;">​</a></h3><p><strong>Issue:</strong> Installer appears to hang during installation</p><p><strong>Solution:</strong></p><ol><li>Wait (installation can take several minutes)</li><li>Check Unity&#39;s progress bar</li><li>Check Console for errors</li><li>If truly hung, restart Unity and try again</li></ol><h3 id="srp-not-detected" tabindex="-1">SRP Not Detected <a class="header-anchor" href="#srp-not-detected" aria-label="Permalink to &quot;SRP Not Detected&quot;">​</a></h3><p><strong>Issue:</strong> Installer says SRP not found</p><p><strong>Solution:</strong></p><ol><li>Install URP or HDRP via Package Manager</li><li>Create and assign SRP asset in Project Settings</li><li>Restart installer</li><li>See <a href="/getting-started/prerequisites.html">Prerequisites</a> for details</li></ol><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><p>If installation/update issues persist:</p><ol><li>Check <a href="/troubleshooting/troubleshooting.html">Troubleshooting</a> guide</li><li>Verify <a href="/getting-started/prerequisites.html">Prerequisites</a></li><li>Check Unity Console for specific errors</li><li>Try clean installation: <ul><li>Remove ProStream package</li><li>Clear Library folder</li><li>Restart Unity</li><li>Reinstall ProStream</li></ul></li></ol><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="/getting-started/installation.html">Installation</a> - Installation guide</li><li><a href="/getting-started/prerequisites.html">Prerequisites</a> - Requirements</li><li><a href="/troubleshooting/troubleshooting.html">Troubleshooting</a> - General issues</li></ul>',70)])])}const h=t(s,[["render",n]]);export{g as __pageData,h as default};
